function process_pet_logic(player,actionset,panelid,actionid,data)	
	--print(actionset,panelid,actionid);
	if actionid == "petyjjh" then
		local ps = util.ptpn(player,const.PTP_PET_ACTIVE_POS);
		do_yijianjinhua(player,ps);return;
	end
	if actionid == "usepetjhd" then
		local ps = util.ptpn(player,const.PTP_PET_ACTIVE_POS);
		do_usejinhuadan(player,ps);
	end
	if actionid == "usepetzzd" then
		local ps = util.ptpn(player,const.PTP_PET_ACTIVE_POS);
		do_quality_upgrade(player,ps);
	end
	if string.sub(actionid,1,6) == "petpos" then
		local pos = tonumber(string.sub(actionid,7));
		player:set_temp_param(const.PTP_PET_ACTIVE_POS,pos);
		freshPetPanel(player,pos);
		return;
	end
	if string.sub(actionid,1,9) == "petstatus" then
		local pos = tonumber(string.sub(actionid,10));
		player:set_temp_param(const.PTP_PET_ACTIVE_POS,pos);
		do_change_pet_status(player,pos);
		freshPetPanel(player,pos);
		return;
	end
end
ckpanel.add_listener(ACTIONSET_NAME,process_pet_logic);

function do_usejinhuadan(player,petpos)
	local dnum = player:num_item("守护进化丹",1);
	if dnum > 0 then
		local pet = player:find_pet_by_pos(petpos);
		if pet then
			local pid = pet:get_typeid();
			local pjlv = pet:get_updlv();
			local pjnum = pet:get_updnum();
			local pjdata = pt_data[pid];
			if pjdata then
				if pjlv < pjdata.maxupdlv then
					local pjd = pjdata.quality[pjlv];
					if pjd then
						player:remove_item("守护进化丹",dnum);
						local redo = 0;
						local updv = false;
						pjnum = pjnum + dnum*300;
						--print(pjnum,pjd.needexp)
						while pjnum >= pjd.needexp do
							redo  = redo + 1;
							if redo >= 100 then break; end
							pjnum = pjnum - pjd.needexp;
							pjlv = pjlv+1;updv = true;
							pjd = pjdata.quality[pjlv];
							if pjlv >= pjdata.maxupdlv then break; end
						end
						pet:set_updlv(pjlv);
						pet:set_updnum(pjnum);
						freshPetPanel(player,petpos);--刷新面板
						player:alert(11,1,"恭喜您进化成功,获得成长经验"..(dnum*300));
						if updv then player:recal_attr();end
					end
				else
					player:alert(11,1,"您的守护进化成长等级已达上线!");
				end
			end
		else
			player:alert(11,1,"您尚未拥有该守护!");
		end
	else
		player:alert(11,1,"您背包中没有守护进化丹!");
	end
end

function do_useExpDan(player,petpos,addexp)
	if addexp > 0 then
		local pet = player:find_pet_by_pos(petpos);
		if pet then
			local pid = pet:get_typeid();
			local plv = pet:get_lv();
			local pexp = pet:get_exp() + addexp;
			
			local pjdata = pt_data[pid];
			if pjdata then
				if plv < pjdata.maxlv then
					--print(plv,pjdata.maxlv);
					local needexp = player:get_exp_need(plv+1);
					local redo = 0;
					local updv = false;
					while pexp >= needexp do
						redo  = redo + 1;
						if redo >= 100 then break; end
						pexp = pexp - needexp;
						plv = plv+1;updv = true;
						needexp = player:get_exp_need(plv+1);
						if plv >= pjdata.maxlv then break; end
					end
					if pexp < 0 then pexp = 0; end
					pet:set_exp(pexp);
					pet:set_lv(plv);
					if updv then
						player:recal_attr();
					end
					freshPetPanel(player,petpos);--刷新面板
				else
					player:alert(11,1,"您的守护等级已达上限!");
				end
			end
		else
			player:alert(11,1,"您尚未拥有该守护!");
		end
	else
		player:alert(11,1,"您背包中没有守护进化丹!");
	end
end

function do_exp_uplevel(player,petpos)
	local pet = player:find_pet_by_pos(petpos);
	if pet then
		local pid = pet:get_typeid();
		local plv = pet:get_lv();
		local pjdata = pt_data[pid];
		if pjdata then
			if plv < pjdata.maxlv then
				pet:set_lv(plv+1);
				player:recal_attr();
