function do_quality_upgrade(player,petpos)
	local pet = player:find_pet_by_pos(petpos);
	if pet then
		local pid = pet:get_typeid();
		local luck = pet:get_luck();
		local quality = pet:get_quality();
		local pjdata = pt_data[pid];
		if pjdata then
			if quality < pjdata.maxzz then
				if player:num_item("守护资质丹",1) >= 1 then
					player:remove_item("守护资质丹",1);
					if luck < pjdata.maxluck then
						local rd = get_pet_quality(petpos);
						--print("1",luck,pjdata.maxluck,rd);
						local nLuckAdd = 1
						if activity.isPetZiZhiDanOpen() then
							nLuckAdd = nLuckAdd * 2
						end
						pet:set_luck(luck+nLuckAdd);
						pet:set_quality(rd);
						player:alert(11,1,"资质进化成功,当前资质"..rd.."%");
					else
						--print("2",luck,pjdata.maxluck,pjdata.maxzz);
						pet:set_quality(pjdata.maxzz);
						player:alert(11,1,"资质进化成功,当前资质"..pjdata.maxzz.."%");
					end
					player:recal_attr();
					freshPetPanel(player,petpos);--刷新面板
				else
					player:alert(11,1,"您背包中没有守护资质丹!");
				end
			else
				player:alert(11,1,"您的守护当前资质已达上限!");
			end
		end
	else
		player:alert(11,1,"您尚未拥有该守护!");
	end
end

