		end
	else
		player:alert(11,1,"您尚未拥有该守护!");return 0;
	end
	return 0;
end

function do_change_pet_status(player,pos)
	for i=1,8 do
		if i == pos then
			local pet = player:find_pet_by_pos(i);
			if pet then
				if util.ppn(player,const.PP_PET_ACTIVE_POS) == pos then
					player:set_param(const.PP_PET_ACTIVE_POS,0);
					player:set_outpet_pos(0);
				else
					player:set_param(const.PP_PET_ACTIVE_POS,pos);
					player:set_outpet_pos(pos);
				end
			end
		end
	end
	for i=1,8 do
		local pet = player:find_pet_by_pos(i);
		if pet then
			local expp = pet:get_exp();
			if expp < 0 then pet:set_exp(0);end
			if util.ppn(player,const.PP_PET_ACTIVE_POS) == i then
				player:set_panel_data(PANEL_ACTIONSET_NAME,"petstatus"..i,"bgid",170021);
				player:set_panel_data(PANEL_ACTIONSET_NAME,"petstatus"..i,"y",-8);
			else
				player:set_panel_data(PANEL_ACTIONSET_NAME,"petstatus"..i,"bgid",170019);
				player:set_panel_data(PANEL_ACTIONSET_NAME,"petstatus"..i,"y",0);
			end
		end
	end
	player:push_ckpanel_data(ACTIONSET_NAME,PANEL_ACTIONSET_NAME);
end

function freshPetPanel(player,petpos)
	for i=1,8 do
		local pet = player:find_pet_by_pos(i);
		if pet then
			if util.ppn(player,const.PP_PET_ACTIVE_POS) == i then
				player:set_panel_data(PANEL_ACTIONSET_NAME,"petstatus"..i,"bgid",170021);
				player:set_panel_data(PANEL_ACTIONSET_NAME,"petstatus"..i,"y",-8);
			else
				player:set_panel_data(PANEL_ACTIONSET_NAME,"petstatus"..i,"bgid",170019);
				player:set_panel_data(PANEL_ACTIONSET_NAME,"petstatus"..i,"y",0);
			end
			player:set_panel_data(PANEL_ACTIONSET_NAME,"petpos"..i,"visible","true");
		else
			player:set_panel_data(PANEL_ACTIONSET_NAME,"petpos"..i,"visible","false");
		end
	end
	if petpos > 0 then
		local pety = player:find_pet_by_pos(petpos);
		if pety then
			local pet_lv = pety:get_lv();
			local upd_lv = pety:get_updlv();
			local luck = pety:get_luck();
			local quality = pety:get_quality();
			local upd_num = pety:get_updnum();--
			local pexp = pety:get_exp();
			local pid = pety:get_typeid();
			local maxatt = pet_lv*2.5;
			local maxdef = pet_lv*0.5;
			local rx = pet_lv*0;
			local pdata = pt_data[pid];

			local pdc,pdc2,pmc,pmc2,psc,psc2,pac,pac2,pmac,pmac2,prx;
			local temp = 0.5*math.pow(upd_lv,1.5);
			local upd = temp - temp%0.1;--精确到小数点后一位
			local temp2 = 0.5*math.pow((upd_lv+1),1.5);
			local upd2 = temp2 - temp2%0.1;--精确到小数点后一位

			local cwmaxatt = math.floor(maxatt*(1+temp/100));
			local cwminatt = math.floor(0.6*maxatt*(1+temp/100));
			local cwmaxdef = math.floor(maxdef*(1+temp/100));
			local cwmindef = math.floor(0.6*maxdef*(1+temp/100));
			local cwrx = rx*(1+temp/100);cwrx = cwrx-cwrx%0.01;

			local fzmaxatt = math.floor(cwmaxatt*quality/100);
			local fzminatt = math.floor(cwminatt*quality/100);
			local fzmaxdef = math.floor(cwmaxdef*quality/100);
			local fzmindef = math.floor(cwmindef*quality/100);
			local fzrx = cwrx*quality/100;
			local nextexp = player:get_exp_need(pet_lv+1);

			--（(攻击成长*3.6)+(物防成长*3.6)*2+韧性成长*1000）*等级
			local fight = math.floor((pdata.baseatt*3.6+pdata.basedef*7.2+pdata.baserx*1000)*pet_lv*(1+temp/100));
			local fzft = math.floor(fight*quality/100);
			--战斗力
			player:set_panel_data(PANEL_ACTIONSET_NAME,"petft"..petpos,"htmlText",fight);
			player:set_panel_data(PANEL_ACTIONSET_NAME,"petName","htmlText",pdata.name);
			for i=1,8 do				
				if i==petpos then
					player:set_panel_data(PANEL_ACTIONSET_NAME,"petswf"..i,"visible","true");
				else
					player:set_panel_data(PANEL_ACTIONSET_NAME,"petswf"..i,"visible","false");
				end
			end
			--player:set_panel_data(PANEL_ACTIONSET_NAME,"petswf","staticimgid","swf/pet"..petpos..".swf");
			------------
			--等级
			player:set_panel_data(PANEL_ACTIONSET_NAME,"petlv","htmlText",pet_lv);
			------------
			--经验
			player:set_panel_data(PANEL_ACTIONSET_NAME,"petexpprogress","action",(pexp%nextexp/nextexp).."_1");
			player:set_panel_data(PANEL_ACTIONSET_NAME,"petexpprogress","label",pexp.."/"..nextexp);
			--成长
			player:set_panel_data(PANEL_ACTIONSET_NAME,"petupdprogress","action",(upd_num%pt_data[pid].quality[upd_lv].needexp/pt_data[pid].quality[upd_lv].needexp).."_1");
			player:set_panel_data(PANEL_ACTIONSET_NAME,"petupdprogress","label",upd_num.."/"..pt_data[pid].quality[upd_lv].needexp);
			--祝福值
			player:set_panel_data(PANEL_ACTIONSET_NAME,"petquaprogress","action",(luck%pt_data[pid].maxluck/pt_data[pid].maxluck).."_1");
			player:set_panel_data(PANEL_ACTIONSET_NAME,"petquaprogress","label",luck.."/"..pt_data[pid].maxluck);
			------------
			--守护属性
			player:set_panel_data(PANEL_ACTIONSET_NAME,"lvdc","htmlText",cwminatt.."-"..cwmaxatt);
			player:set_panel_data(PANEL_ACTIONSET_NAME,"lvmc","htmlText",cwminatt.."-"..cwmaxatt);
			player:set_panel_data(PANEL_ACTIONSET_NAME,"lvsc","htmlText",cwminatt.."-"..cwmaxatt);
			player:set_panel_data(PANEL_ACTIONSET_NAME,"lvac","htmlText",cwmindef.."-"..cwmaxdef);
			player:set_panel_data(PANEL_ACTIONSET_NAME,"lvmac","htmlText",cwmindef.."-"..cwmaxdef);
			player:set_panel_data(PANEL_ACTIONSET_NAME,"lvrx","htmlText",cwrx.."%");
			player:set_panel_data(PANEL_ACTIONSET_NAME,"lvft","htmlText",fight);
			------------
			--辅主属性
			player:set_panel_data(PANEL_ACTIONSET_NAME,"fzdc","htmlText",fzminatt.."-"..fzmaxatt);
			player:set_panel_data(PANEL_ACTIONSET_NAME,"fzmc","htmlText",fzminatt.."-"..fzmaxatt);
			player:set_panel_data(PANEL_ACTIONSET_NAME,"fzsc","htmlText",fzminatt.."-"..fzmaxatt);
			player:set_panel_data(PANEL_ACTIONSET_NAME,"fzac","htmlText",fzmindef.."-"..fzmaxdef);
			player:set_panel_data(PANEL_ACTIONSET_NAME,"fzmac","htmlText",fzmindef.."-"..fzmaxdef);
			player:set_panel_data(PANEL_ACTIONSET_NAME,"fzrx","htmlText",fzrx.."%");
			player:set_panel_data(PANEL_ACTIONSET_NAME,"fzft","htmlText",fzft);
			------------
			--成长等级
			player:set_panel_data(PANEL_ACTIONSET_NAME,"uplvtxt","htmlText",upd_lv.."/"..pdata.maxupdlv);
			------------
			--成长进度
			--player:set_panel_data(PANEL_ACTIONSET_NAME,"petyjjh","tooltip_text","");
			------------
			--当前进化属性
			player:set_panel_data(PANEL_ACTIONSET_NAME,"cupname","zzlvtxt",upd.."%");
			player:set_panel_data(PANEL_ACTIONSET_NAME,"cupdc","htmlText",upd.."%");
			player:set_panel_data(PANEL_ACTIONSET_NAME,"cupmc","htmlText",upd.."%");
			player:set_panel_data(PANEL_ACTIONSET_NAME,"cupsc","htmlText",upd.."%");
			player:set_panel_data(PANEL_ACTIONSET_NAME,"cupac","htmlText",upd.."%");
			player:set_panel_data(PANEL_ACTIONSET_NAME,"cupmac","htmlText",upd.."%");
			player:set_panel_data(PANEL_ACTIONSET_NAME,"cuprx","htmlText",upd.."%");
			------------
			--下一阶进化属性
			player:set_panel_data(PANEL_ACTIONSET_NAME,"nupname","zzlvtxt",upd2.."%");
			player:set_panel_data(PANEL_ACTIONSET_NAME,"nupdc","htmlText",upd2.."%");
			player:set_panel_data(PANEL_ACTIONSET_NAME,"nupmc","htmlText",upd2.."%");
			player:set_panel_data(PANEL_ACTIONSET_NAME,"nupsc","htmlText",upd2.."%");
			player:set_panel_data(PANEL_ACTIONSET_NAME,"nupac","htmlText",upd2.."%");
			player:set_panel_data(PANEL_ACTIONSET_NAME,"nupmac","htmlText",upd2.."%");
			player:set_panel_data(PANEL_ACTIONSET_NAME,"nuprx","htmlText",upd2.."%");
			------------
			--资质
			player:set_panel_data(PANEL_ACTIONSET_NAME,"maxzizhi","htmlText",pt_data[pid].maxzz.."%");
			player:set_panel_data(PANEL_ACTIONSET_NAME,"czizhi","htmlText",quality.."%");
			player:set_panel_data(PANEL_ACTIONSET_NAME,"lucktxt","htmlText",luck);
			------------
		end
	end
	player:push_ckpanel_data(ACTIONSET_NAME,PANEL_ACTIONSET_NAME);
end

function open_pet_panel(player)
	local zjie = util.ppn(player,const.PP_NEW_ZUOQI_JIE_LEVEL);	
	if zjie >= 6 then
		local pet = player:find_pet_by_pos(1);
		if not pet then
			player:add_pet(10000,1,1,1,1,50,101);
			player:set_param(const.PP_PET_ACTIVE_POS,1);			
			player:set_outpet_pos(1);
		end

		player:set_panel_data("panelpet","panelpet","panel_visible","true");
		freshPetPanel(player,1);
		player:recal_attr();
	else
		player:alert(11,1,"坐骑等级达到6级后开放守护功能!");
	end
end

function give_pet(player,ptype,ppos,lv,updlv,zlv,qua,src)--10000,1,1,1,1,50,101
	local pet = player:find_pet_by_pos(ppos);
	if pet then
		player:alert(11,1,"已经拥有该守护!");
	else
		player:add_pet(ptype,ppos,lv,updlv,zlv,qua,src);
		player:set_param(const.PP_PET_ACTIVE_POS,ppos);
		player:set_outpet_pos(ppos);
	end
	player:recal_attr();
	freshPetPanel(player,ppos);
end

function resetPetOutPos(player)
	local pt = util.ppn(player,const.PP_PET_ACTIVE_POS);
	if pt > 0 then
		player:set_outpet_pos(pt);
		player:set_temp_param(const.PTP_PET_ACTIVE_POS,pt);
	end
end
login.add_login_listener(resetPetOutPos);